package delene;

public class Logika {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean a = true;
		boolean b = false; 
		System.out.println(a && b);
		System.out.println(a || b);
		System.out.println(!b);
		System.out.println(b || true);
		System.out.println((5>7) ^ (a==b));
	}

}
